import Foundation

func solution(_ s:String) -> [Int] {
    var s = s
    var count = 0
    var totalZeroCount = 0
    
    while s.count > 1 {
        let zeroCount = s.filter { $0 == "0" }.count
        totalZeroCount += zeroCount
        let removeZeroStrCount = s.count - zeroCount
        s = String(removeZeroStrCount, radix: 2)
        
        count += 1
    }
    
    return [count, totalZeroCount]
}

print(solution(""))
